jQuery(document).ready(function(){
	// Menú principal
	jQuery(window).scroll(function(){
		resizeNav();
	});

	// Cookies
	if (!ces_check_visibility())
	{
		jQuery('body').append('<div id="cookies"><div class="cookies_es_inner"><p>Utilizamos cookies propias y de terceros con fines estad&iacute;sticos para poder mejorar tu navegaci&oacute;n y nuestros servicios.</p><p>Si contin&uacute;as navegando, consideramos que aceptas su uso. Para m&aacute;s informaci&oacute;n, consulta nuestra <a href="/politica-cookies" target="_blank" title="Revisar pol&iacute;tica de cookies">pol&iacute;tica de cookies</a>.</p><p><a class="cookies-close" href="#">aceptar</a></p></div></div>');

		jQuery('.cookies-close').click(function(e){
			e.preventDefault();
			jQuery('#cookies').fadeOut();
		});

		// El mensaje aparecerá sólo la primera vez
		ces_visibility_off(60);
	}


	// Enlaces externos
	jQuery('a[rel*=external]').click(function() {
		window.open(this.href);
		return false;
	});
	jQuery('area[rel*=external]').click(function() {
		window.open(this.href);
		return false;
	});
	jQuery.each(jQuery('a[target*=_blank],a[rel*=external],area[rel*=external]'), function(index, value) {
		var title = jQuery(this).attr('title');

		if (typeof title === 'undefined') jQuery(this).attr('title', 'este enlace se abrirá en una ventana emergente');
		else jQuery(this).attr('title', title + ' (este enlace se abrirá en una ventana emergente)');
	});
});